World::~World()
{
}

void World::Draw(sf::RenderWindow & window)
{
	for (int i = 0; i < myTiles.size(); i++)
	{
		window.draw(myTiles[i].GetSprite());
	}
}

Tile World::GetTile(int tileId)
{
	return myTiles[tileId];
}



void World::Update(sf::RenderWindow & window)
{
	Draw(window);
}