	{
		if (x >= tileCountWidth)
		{
			x = 0;
			y++;
		}
		int mapTileCount = x + y * tileCountWidth; //det antal nummer på den tile där
		switch (map[mapTileCount])
		{
		case 0:
		{
			myTiles.push_back(Tile(grass, sf::Vector2f(grass.getPosition().x + (x * grass.getGlobalBounds().width), grass.getPosition().y + (y * grass.getGlobalBounds().height)), Grass));
			break;
		}
		case 1:
		{
			myTiles.push_back(Tile(water, sf::Vector2f(water.getPosition().x + (x * water.getGlobalBounds().width), water.getPosition().y + (y * water.getGlobalBounds().height)), Water));

			break;
		}
		case 2:
		{
			myTiles.push_back(Tile(dirt, sf::Vector2f(dirt.getPosition().x + (x * dirt.getGlobalBounds().width), dirt.getPosition().y + (y * dirt.getGlobalBounds().height)), Dirt));

			break;
		}
		case 3:
		{
			myTiles.push_back(Tile(lava, sf::Vector2f(lava.getPosition().x + (x * lava.getGlobalBounds().width), lava.getPosition().y + (y * lava.getGlobalBounds().height)), Lava));

			break;
		}
		case 4:
		{
			myTiles.push_back(Tile(empty, sf::Vector2f(empty.getPosition().x + (x * empty.getGlobalBounds().width), empty.getPosition().y + (y * empty.getGlobalBounds().height)), Empty));

			break;
		}
		}
	}
	for (int i = 0; i < myTiles.size(); i++)
	{
		if (myTiles[i].GetType() == Lava)
		{
			conCreteTiles.push_back(myTiles[i]);
		}
	}
