sf::IntRect World::GetRect(sf::Sprite aSprite)
{
	return sf::IntRect(aSprite.getPosition().x, aSprite.getPosition().y, aSprite.getTextureRect().width * aSprite.getScale().x, aSprite.getTextureRect().height * aSprite.getScale().y);
}

